pragma solidity >=0.5.16;


contract Lotto {
    event Winning_option(uint256 option);

    // unix timestamp representing the time when the current
    // lottery ends and a new one starts.
    uint256[10] private nextDrawingTime;
    // number of options available in the current lottery
    uint256[10] private nOptions;
    // history of options which have won the lottery
    uint256[10] private winningOption;
    // pointer to the current head-location in nOptions and nextDrawingTime
    uint256 private head = 0;
    // note: nHistory is the length of the history which is stored. This number is hard coded
    // in the arrays `nextDrawingTime` and `nOptions` due to the fact that its not possible to
    // create an array of static length using a variable as the indicator of the length.
    // explanation at: https://ethereum.stackexchange.com/a/12434
    uint256 private nHistory = 10;

    struct Bet {
        address payable user; // the users ethereum address who has placed the bet
        uint256 weight_wei; // amount of ether spent in wei
        uint256 option_idx; // index into the option array
    }

    Bet[] public bets; // list of bets for the current Lottery
    Bet[] public prevBets; // list of bets from the previous Lottery

    mapping(address => bool) public hasPlacedBet; // mapping to check if a user has already set a bet

    constructor(uint256 _nOptions, uint256 _nextDrawingTime) public {
        nOptions[head] = _nOptions;
        nextDrawingTime[head] = _nextDrawingTime;
    }

    function nBets() public view returns (uint256) {
        return bets.length;
    }

    function getNextDrawingTime(uint256 _idx) public view returns (uint) {
        uint256 idx = (head - _idx) % nHistory;
        return nextDrawingTime[idx];
    }

    function getNOptions(uint256 _idx) public view returns (uint) {
        uint256 idx = (head - _idx) % nHistory;
        return nOptions[idx];
    }

    function getWinningOption(uint256 _idx) public view returns (uint) {
        uint256 idx = (head - _idx) % nHistory;
        return winningOption[idx];
    }

    function getBetsOf(Bet[] storage _bets) internal view returns (address[] memory, uint[] memory, uint[] memory) {
        address[] memory addrs = new address[](_bets.length);
        uint[] memory weights = new uint[](_bets.length);
        uint[] memory opt_idcs = new uint[](_bets.length);

        for (uint256 i = 0; i < _bets.length; i++) {
            Bet storage bet = _bets[i];
            addrs[i] = bet.user;
            weights[i] = bet.weight_wei;
            opt_idcs[i] = bet.option_idx;
        }

        return (addrs, weights, opt_idcs);
    }

    function getBets() public view returns (address[] memory, uint[] memory, uint[] memory) {
        return getBetsOf(bets);
    }

    function getPrevBets() public view returns (address[] memory, uint[] memory, uint[] memory) {
        return getBetsOf(prevBets);
    }

    function _resetLottery(uint256 _nOptions, uint256 _nextDrawingTime) private {
        for (uint256 i = 0; i < bets.length; i++) {
            delete hasPlacedBet[bets[i].user];
        }
        delete prevBets;

        prevBets = bets;
        delete bets;

        head = (head + 1) % nHistory;
        nextDrawingTime[head] = _nextDrawingTime;
        nOptions[head] = _nOptions;
    }

    function _eval() private {
        if (nBets() == 0) {
            return;
        }

        // for every option, sum all the money (weigh_wei) up and save it in
        // the temporary array weightPerOption.
        uint256[] memory weightPerOption = new uint256[](nOptions[head]);
        for (uint256 i = 0; i < bets.length; i++) {
            Bet storage bet = bets[i];
            weightPerOption[bet.option_idx] += bet.weight_wei;
        }

        // create a cumulative sum array named cumWeight over weightPerOption.
        uint256[] memory cumWeight = new uint256[](nOptions[head]);
        cumWeight[0] = weightPerOption[0];
        for (uint256 i = 1; i < nOptions[head]; i++) {
            cumWeight[i] = cumWeight[i - 1] + weightPerOption[i];
        }

        // sum_wei is the sum of all the money that has been bet in this lottery
        uint256 sum_wei = cumWeight[nOptions[head] - 1];

        // this assert holds becaus if no bet has been place we have already returned and
        // it is not possible to place a bet with a weight of 0.
        assert(sum_wei > 0);
        // todo: subtract our own money from sum_wei

        // winning_value is a random number in range [0, sum_wei-1].
        // winning_option is the option (a number in range [0, nOptions[head]-1]) which has won the lottery.
        uint256 winning_value = uint256(
            keccak256(abi.encodePacked(now, msg.sender))
        ) % sum_wei;
        uint256 winning_option;
        for (uint256 i = 0; i < nOptions[head]; i++) {
            if (winning_value < cumWeight[i]) {
                winning_option = i;
                emit Winning_option(winning_option);
                winningOption[head] = winning_option;
                break;
            }
        }

        // pay out every user who has bet on the winning_option.
        // the money he receives is a fraction of all the money in the jackpot:
        // the fraction is what the current user has bet on the winning_option (bet.weight_wei)
        // compared to all the money that has been bet on the winning_option (winning_option_sum)
        uint256 winning_option_sum = weightPerOption[winning_option];
        for (uint256 i = 0; i < bets.length; i++) {
            Bet storage bet = bets[i];
            if (bet.option_idx == winning_option) {
                // using call.value() due to: https://ethereum.stackexchange.com/a/19343
                assert(bet.weight_wei <= winning_option_sum);
                // uint256 fraction = bet.weight_wei / winning_option_sum; // note: if not divisible, remainder of money is kept
                (bool success, ) = bet.user.call.value(sum_wei * bet.weight_wei / winning_option_sum)("");
                require(success, "Transaction failed");
            }
        }
    }

    function eval_reset(uint256 _nOptions, uint256 _nextDrawingTime) public {
        require(nextDrawingTime[head] < now, string(abi.encodePacked("the current lottery is not closed yet")));
        require(_nOptions > 1, "more than one options are required to choose from");
        require(_nextDrawingTime > now, "the nextDrawingTime must be in the future");
        _eval();
        _resetLottery(_nOptions, _nextDrawingTime);
    }

    function placeBet(uint256 option_idx) public payable {
        require(0 <= option_idx && option_idx < nOptions[head], string(abi.encodePacked(option_idx, " is not a valid option")));
        require(now < nextDrawingTime[head], "the current lottery is closed for bets. waiting for evaluation.");
        require(!hasPlacedBet[msg.sender], "sender has already placed a bet in the current lottery");
        require(msg.value > 0, "to place a bet, the msg.value (the money being bet) must be > 0");
        bets.push(Bet(msg.sender, msg.value, option_idx));
        hasPlacedBet[msg.sender] = true;
    }

    function viewMyBet() public view returns (address, uint, uint) {
        require(hasPlacedBet[msg.sender], "user has not placed a bet yet");
        uint256 numBets = nBets();
        for (uint256 i = 0; i < numBets; i++) {
            Bet storage bet = bets[i];
            if (bet.user == msg.sender) {
                return (bet.user, bet.weight_wei, bet.option_idx);
            }
        }
    }
}
